mapper.select:
SELECT COUNT(*)
FROM topics


        user_id = #{userId}


        AND category_id = #{categoryId}


        AND status = #{status}


        AND (title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))

mapper.update:
UPDATE topics
title = #{title}, content = #{content}, cover_url = #{coverUrl}, category_id = #{categoryId}, status = #{status}, is_pinned = #{isPinned}, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT *
FROM topics


        user_id = #{userId}


        AND category_id = #{categoryId}


        AND status = #{status}


        AND (title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))




        ORDER BY views DESC


        ORDER BY likes DESC


        ORDER BY comments DESC


        ORDER BY is_pinned DESC, created_at DESC



    LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM topics


        AND user_id = #{userId}


        AND category_id = #{categoryId}


        AND status = #{status}


        AND (title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))

mapper.update:
UPDATE topics
SET views = views + 1
WHERE id = #{id}

UPDATE topics
SET likes = likes + #{increment}
WHERE id = #{id} 

    AND likes > 0

UPDATE topics
SET comments = comments + #{increment}
WHERE id = #{id}

    AND comments > 0

mapper.select:
SELECT COUNT(*)
FROM topics
WHERE user_id = #{userId}

    AND status = #{status}

SELECT COUNT(*)
FROM topics
WHERE category_id = #{categoryId}

    AND status = #{status}

SELECT COUNT(*) FROM topics WHERE status = 1
AND category_id = #{categoryId}
AND created_at >= DATE_SUB(NOW(), INTERVAL #{days} DAY)
